import React, { useState, useEffect } from 'react';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend } from 'recharts';
import { Search } from 'lucide-react';

const Dashboard = () => {
    const [jobs, setJobs] = useState([]);
    const [stats, setStats] = useState(null);
    const [searchTerm, setSearchTerm] = useState('');
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [selectedJobDetails, setSelectedJobDetails] = useState(null);
    const [searchTimeout, setSearchTimeout] = useState(null);

    const fetchAllData = async () => {
        try {
            setLoading(true);
            setError(null);

            const [jobsResponse, statsResponse] = await Promise.all([
                fetch('http://localhost:5128/api/dashboard/jobs'),
                fetch('http://localhost:5128/api/dashboard/stats')
            ]);

            if (!jobsResponse.ok || !statsResponse.ok) {
                throw new Error(`HTTP error! Jobs: ${jobsResponse.status}, Stats: ${statsResponse.status}`);
            }

            const jobsData = await jobsResponse.json();
            const statsData = await statsResponse.json();

            setJobs(jobsData);
            setStats(statsData);
            setSelectedJobDetails(null); // Clear job details when fetching all jobs
        } catch (error) {
            console.error('Error fetching data:', error);
            setError(error.message);
        } finally {
            setLoading(false);
        }
    };

    const fetchJobDetails = async (jobNumber) => {
        try {
            setLoading(true);
            const response = await fetch(`http://localhost:5128/api/dashboard/jobs/${jobNumber}`);

            if (!response.ok) {
                throw new Error(`HTTP error! Status: ${response.status}`);
            }

            const detailsData = await response.json();
            setSelectedJobDetails(detailsData);
            setJobs([{
                jobNumber: parseInt(jobNumber),
                totalParts: detailsData.length,
                totalQuantity: detailsData.reduce((sum, item) => sum + item.quantity, 0),
                totalScanned: detailsData.reduce((sum, item) => sum + item.scannedQuantity, 0),
                lastScanDate: detailsData[0]?.scanDate
            }]);
        } catch (error) {
            console.error('Error fetching job details:', error);
            setError(error.message);
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchAllData();
        const interval = setInterval(fetchAllData, 30000);
        return () => clearInterval(interval);
    }, []);

    // Handle search input changes with debouncing
    const handleSearchChange = (e) => {
        const value = e.target.value;
        setSearchTerm(value);

        // Clear the existing timeout
        if (searchTimeout) {
            clearTimeout(searchTimeout);
        }

        // If search is cleared, fetch all data
        if (!value.trim()) {
            fetchAllData();
            return;
        }

        // Set new timeout for search
        const newTimeout = setTimeout(() => {
            const jobNumber = parseInt(value);
            if (!isNaN(jobNumber)) {
                fetchJobDetails(jobNumber);
            }
        }, 500); // 500ms delay

        setSearchTimeout(newTimeout);
    };

    if (loading) {
        return (
            <div className="flex items-center justify-center min-h-screen">
                <div className="text-xl">Loading dashboard...</div>
            </div>
        );
    }

    if (error) {
        return (
            <div className="flex items-center justify-center min-h-screen">
                <div className="text-xl text-red-600">Error: {error}</div>
            </div>
        );
    }

    return (
        <div className="p-6 max-w-7xl mx-auto">
            {/* Stats Overview */}
            <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-6">
                <div className="bg-white p-4 rounded-lg shadow">
                    <h3 className="text-gray-500 text-sm">Total Jobs</h3>
                    <p className="text-2xl font-bold">{stats?.totalJobs || 0}</p>
                </div>
                <div className="bg-white p-4 rounded-lg shadow">
                    <h3 className="text-gray-500 text-sm">Scanned Items</h3>
                    <p className="text-2xl font-bold">{stats?.totalScannedItems || 0}</p>
                </div>
                <div className="bg-white p-4 rounded-lg shadow">
                    <h3 className="text-gray-500 text-sm">Unique Parts</h3>
                    <p className="text-2xl font-bold">{stats?.totalUniqueParts || 0}</p>
                </div>
                <div className="bg-white p-4 rounded-lg shadow">
                    <h3 className="text-gray-500 text-sm">Latest Job</h3>
                    <p className="text-2xl font-bold">{stats?.latestJob || '-'}</p>
                </div>
            </div>

            {/* Search Bar */}
            <div className="mb-6 relative">
                <Search className="absolute left-3 top-3 text-gray-400" size={20} />
                <input
                    type="text"
                    placeholder="Search by job number..."
                    className="w-full pl-10 pr-4 py-2 border rounded-lg"
                    value={searchTerm}
                    onChange={handleSearchChange}
                />
            </div>

            {/* Chart */}
            <div className="bg-white p-4 rounded-lg shadow mb-6">
                <h2 className="text-lg font-semibold mb-4">Recent Jobs Progress</h2>
                <BarChart width={800} height={300} data={jobs.slice(0, 10)}>
                    <CartesianGrid strokeDasharray="3 3" />
                    <XAxis dataKey="jobNumber" />
                    <YAxis />
                    <Tooltip />
                    <Legend />
                    <Bar dataKey="totalQuantity" fill="#8884d8" name="Total Quantity" />
                    <Bar dataKey="totalScanned" fill="#82ca9d" name="Scanned" />
                </BarChart>
            </div>

            {/* Jobs Table */}
            <div className="bg-white rounded-lg shadow overflow-hidden">
                {selectedJobDetails ? (
                    <table className="min-w-full">
                        <thead className="bg-gray-50">
                            <tr>
                                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Part ID</th>
                                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Quantity</th>
                                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Scanned</th>
                                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Scan Date</th>
                            </tr>
                        </thead>
                        <tbody className="divide-y divide-gray-200">
                            {selectedJobDetails.map((detail, index) => (
                                <tr key={index}>
                                    <td className="px-6 py-4">{detail.partId}</td>
                                    <td className="px-6 py-4">{detail.quantity}</td>
                                    <td className="px-6 py-4">{detail.scannedQuantity}</td>
                                    <td className="px-6 py-4">
                                        {new Date(detail.scanDate).toLocaleString()}
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                ) : (
                    <table className="min-w-full">
                        <thead className="bg-gray-50">
                            <tr>
                                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Job Number</th>
                                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Total Parts</th>
                                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Progress</th>
                                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Last Scan</th>
                            </tr>
                        </thead>
                        <tbody className="divide-y divide-gray-200">
                            {jobs.map((job) => (
                                <tr key={job.jobNumber}>
                                    <td className="px-6 py-4">{job.jobNumber}</td>
                                    <td className="px-6 py-4">{job.totalParts}</td>
                                    <td className="px-6 py-4">
                                        <div className="w-full bg-gray-200 rounded-full h-2.5">
                                            <div
                                                className="bg-blue-600 h-2.5 rounded-full"
                                                style={{ width: `${(job.totalScanned / job.totalQuantity * 100)}%` }}
                                            ></div>
                                        </div>
                                        <span className="text-sm text-gray-500">
                                            {job.totalScanned} / {job.totalQuantity}
                                        </span>
                                    </td>
                                    <td className="px-6 py-4">
                                        {new Date(job.lastScanDate).toLocaleString()}
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                )}
            </div>
        </div>
    );
};

export default Dashboard;
